This sample shows how to call GetConnectionsAsync and parse the result.

Uri endpoint = new Uri("<endpoint>");
AzureKeyCredential credential = new AzureKeyCredential("<key>");
AI.Projects.1DP.Connections client = new Projects.1DP.AIProjectClient(endpoint, credential).GetConnectionsClient(apiVersion: "2025-05-15-preview");

await foreach (BinaryData item in client.GetConnectionsAsync((string)null, (bool?)null, (int?)null, (int?)null, (int?)null, (RequestContext)null))
{
    JsonElement result = JsonDocument.Parse(item.ToStream()).RootElement;
    Console.WriteLine(result.GetProperty("authType").ToString());
    Console.WriteLine(result.GetProperty("name").ToString());
    Console.WriteLine(result.GetProperty("type").ToString());
    Console.WriteLine(result.GetProperty("target").ToString());
    Console.WriteLine(result.GetProperty("isDefault").ToString());
    Console.WriteLine(result.GetProperty("credentials").GetProperty("authType").ToString());
    Console.WriteLine(result.GetProperty("metadata").GetProperty("<key>").ToString());
}

