member.example:
This sample shows how to call CreateOrUpdateAsync.

Uri endpoint = new Uri("<endpoint>");
AzureKeyCredential credential = new AzureKeyCredential("<key>");
DataSources client = new SearchClient(endpoint, credential).GetDataSourcesClient();

SearchIndexerDataSource dataSource = new SearchIndexerDataSource("tempdatasource", SearchIndexerDataSourceType.AzureBlob, new DataSourceCredentials
{
    ConnectionString = "DefaultEndpointsProtocol=https;AccountName=myAccountName;AccountKey=myAccountKey;EndpointSuffix=core.windows.net ",
}, new SearchIndexerDataContainer("doc-extraction-skillset")
{
    Query = "E2E_Dsat",
})
{
    Description = "My Azure Blob data source.",
    Identity = new SearchIndexerDataNoneIdentity(),
    DataChangeDetectionPolicy = new HighWaterMarkChangeDetectionPolicy("metadata_storage_last_modified"),
    DataDeletionDetectionPolicy = new SoftDeleteColumnDeletionDetectionPolicy
    {
        SoftDeleteColumnName = "isDeleted",
        SoftDeleteMarkerValue = "true",
    },
    ETag = "0x1234568AE7E58A1",
    EncryptionKey = new SearchResourceEncryptionKey("myUserManagedEncryptionKey-createdinAzureKeyVault", "https://myKeyVault.vault.azure.net")
    {
        KeyVaultKeyVersion = "myKeyVersion-32charAlphaNumericString",
        AccessCredentials = new AzureActiveDirectoryApplicationCredentials("00000000-0000-0000-0000-000000000000")
        {
            ApplicationSecret = "<applicationSecret>",
        },
    },
};
Response<SearchIndexerDataSource> response = await client.CreateOrUpdateAsync("tempdatasource", dataSource);

