This sample shows how to call CreateAsync and parse the result.

Uri endpoint = new Uri("<endpoint>");
AzureKeyCredential credential = new AzureKeyCredential("<key>");
Indexes client = new SearchClient(endpoint, credential).GetIndexesClient();

using RequestContent content = RequestContent.Create(new Dictionary<string, object>
{
    ["name"] = "temp-preview-test",
    ["fields"] = new object[]
    {
        new
        {
            name = "id",
            type = "Edm.String",
            key = true,
            sortable = true,
        },
        new
        {
            name = "vector1",
            type = "Collection(Edm.Single)",
            retrievable = true,
            searchable = true,
            dimensions = 20,
            vectorSearchProfile = "config1",
        },
        new
        {
            name = "vector1b",
            type = "Collection(Edm.Single)",
            retrievable = true,
            searchable = true,
            dimensions = 10,
            vectorSearchProfile = "config2",
        },
        new
        {
            name = "vector2",
            type = "Collection(Edm.Single)",
            retrievable = true,
            searchable = true,
            dimensions = 5,
            vectorSearchProfile = "config3",
        },
        new
        {
            name = "vector3",
            type = "Collection(Edm.Single)",
            retrievable = true,
            searchable = true,
            dimensions = 5,
            vectorSearchProfile = "config3",
        },
        new
        {
            name = "vector22",
            type = "Collection(Edm.Single)",
            retrievable = true,
            searchable = true,
            dimensions = 10,
            vectorSearchProfile = "config2",
        },
        new
        {
            name = "vector4",
            type = "Collection(Edm.Single)",
            retrievable = true,
            searchable = true,
            dimensions = 32,
            vectorSearchProfile = "config4",
        },
        new
        {
            name = "name",
            type = "Edm.String",
            retrievable = true,
            searchable = true,
            filterable = true,
            sortable = true,
            facetable = true,
            analyzer = "en.lucene",
        },
        new
        {
            name = "description",
            type = "Edm.String",
            retrievable = true,
            searchable = true,
            filterable = true,
            sortable = true,
            facetable = true,
            analyzer = "standard.lucene",
        },
        new
        {
            name = "category",
            type = "Edm.String",
            retrievable = true,
            searchable = true,
            filterable = true,
            sortable = true,
            facetable = true,
            analyzer = "en.lucene",
        },
        new
        {
            name = "ownerId",
            type = "Edm.String",
            retrievable = true,
            searchable = true,
            filterable = true,
            sortable = true,
            facetable = true,
            analyzer = "en.lucene",
        },
        new
        {
            name = "price",
            type = "Edm.Double",
            retrievable = true,
            filterable = true,
            sortable = true,
            facetable = true,
        }
    },
    ["scoringProfiles"] = new object[]
    {
        new
        {
            name = "stringFieldBoost",
            text = new
            {
                weights = new
                {
                    name = 3,
                    description = 1,
                    category = 2,
                    ownerId = 1,
                },
            },
            functions = new object[]
            {
                new
                {
                    tag = new
                    {
                        tagsParameter = "categoryTag",
                    },
                    type = "tag",
                    fieldName = "category",
                    boost = 2,
                }
            },
        }
    },
    ["defaultScoringProfile"] = "stringFieldBoost",
    ["corsOptions"] = new
    {
        allowedOrigins = new object[]
        {
            "https://www.example.com/foo"
        },
        maxAgeInSeconds = 10L,
    },
    ["suggesters"] = new object[]
    {
        new
        {
            name = "sg",
            searchMode = "analyzingInfixMatching",
            sourceFields = new object[]
            {
                "category",
                "ownerId"
            },
        }
    },
    ["analyzers"] = new object[]
    {
        new Dictionary<string, object>
        {
            ["tokenizer"] = "standard_v2",
            ["tokenFilters"] = new object[]
            {
                "common_grams"
            },
            ["charFilters"] = new object[]
            {
                "html_strip"
            },
            ["@odata.type"] = "#Microsoft.Azure.Search.CustomAnalyzer",
            ["name"] = "tagsAnalyzer"
        }
    },
    ["tokenizers"] = new object[]
    {
        new Dictionary<string, object>
        {
            ["maxTokenLength"] = 100,
            ["@odata.type"] = "#Microsoft.Azure.Search.StandardTokenizerV2",
            ["name"] = "my_tokenizer"
        }
    },
    ["tokenFilters"] = new object[]
    {
        new Dictionary<string, object>
        {
            ["preserveOriginal"] = false,
            ["@odata.type"] = "#Microsoft.Azure.Search.AsciiFoldingTokenFilter",
            ["name"] = "my_tokenFilter"
        }
    },
    ["charFilters"] = new object[]
    {
        new Dictionary<string, object>
        {
            ["mappings"] = new object[]
            {
                ".=>,",
                "_=>-"
            },
            ["@odata.type"] = "#Microsoft.Azure.Search.MappingCharFilter",
            ["name"] = "my_mapping"
        }
    },
    ["normalizers"] = new object[]
    {
        new Dictionary<string, object>
        {
            ["tokenFilters"] = new object[]
            {
                "my_tokenFilter"
            },
            ["charFilters"] = new object[]
            {
                "my_mapping"
            },
            ["@odata.type"] = "#Microsoft.Azure.Search.CustomNormalizer",
            ["name"] = "my_normalizer"
        }
    },
    ["similarity"] = new Dictionary<string, object>
    {
        ["k1"] = 10,
        ["b"] = 0.1,
        ["@odata.type"] = "#Microsoft.Azure.Search.BM25Similarity"
    },
    ["semantic"] = new
    {
        defaultConfiguration = "testconfig",
        configurations = new object[]
        {
            new
            {
                name = "testconfig",
                prioritizedFields = new
                {
                    titleField = new
                    {
                        fieldName = "category",
                    },
                    prioritizedContentFields = new object[]
                    {
                        new
                        {
                            fieldName = "description",
                        }
                    },
                    prioritizedKeywordsFields = new object[]
                    {
                        new
                        {
                            fieldName = "ownerId",
                        }
                    },
                },
                flightingOptIn = true,
            }
        },
    },
    ["vectorSearch"] = new
    {
        profiles = new object[]
        {
            new
            {
                name = "config1",
                algorithm = "cosine",
                vectorizer = "openai",
                compression = "mySQ8",
            },
            new
            {
                name = "config2",
                algorithm = "euclidean",
                vectorizer = "custom-web-api",
                compression = "mySQ8",
            },
            new
            {
                name = "config3",
                algorithm = "dotProduct",
                vectorizer = "custom-web-api",
                compression = "myBQC",
            },
            new
            {
                name = "config4",
                algorithm = "dotProduct",
                vectorizer = "custom-web-api",
                compression = "myBQWithoutOriginals",
            }
        },
        algorithms = new object[]
        {
            new
            {
                hnswParameters = new
                {
                    metric = "cosine",
                },
                name = "cosine",
                kind = "hnsw",
            },
            new
            {
                hnswParameters = new
                {
                    metric = "euclidean",
                },
                name = "euclidean",
                kind = "hnsw",
            },
            new
            {
                hnswParameters = new
                {
                    metric = "dotProduct",
                },
                name = "dotProduct",
                kind = "hnsw",
            }
        },
        vectorizers = new object[]
        {
            new
            {
                azureOpenAIParameters = new
                {
                    resourceUri = "https://test-sample.openai.azure.com/",
                    deploymentId = "model",
                    apiKey = "api-key",
                    modelName = "text-embedding-3-large",
                },
                name = "openai",
                kind = "azureOpenAI",
            },
            new
            {
                customWebApiParameters = new
                {
                    uri = "https://my-custom-endpoint.org/",
                    httpHeaders = new
                    {
                        header1 = "value1",
                        header2 = "value2",
                    },
                    httpMethod = "POST",
                    timeout = "PT1M",
                    authResourceId = "api://f89d1c93-58a7-4b07-9a5b-5f89048b927b",
