Uri endpoint = new Uri("<endpoint>");
AzureKeyCredential credential = new AzureKeyCredential("<key>");
Indexes client = new SearchClient(endpoint, credential).GetIndexesClient();

SearchIndex index = new SearchIndex("temp-preview-test", new SearchField[]
{
    new SearchField("id", SearchFieldDataType.String)
    {
        Key = true,
        Sortable = true,
    },
    new SearchField("vector1", new SearchFieldDataType("Collection(Edm.Single)"))
    {
        Retrievable = true,
        Searchable = true,
        Dimensions = 20,
        VectorSearchProfile = "config1",
    },
    new SearchField("vector1b", new SearchFieldDataType("Collection(Edm.Single)"))
    {
        Retrievable = true,
        Searchable = true,
        Dimensions = 10,
        VectorSearchProfile = "config2",
    },
    new SearchField("vector2", new SearchFieldDataType("Collection(Edm.Single)"))
    {
        Retrievable = true,
        Searchable = true,
        Dimensions = 5,
        VectorSearchProfile = "config3",
    },
    new SearchField("vector3", new SearchFieldDataType("Collection(Edm.Single)"))
    {
        Retrievable = true,
        Searchable = true,
        Dimensions = 5,
        VectorSearchProfile = "config3",
    },
    new SearchField("vector22", new SearchFieldDataType("Collection(Edm.Single)"))
    {
        Retrievable = true,
        Searchable = true,
        Dimensions = 10,
        VectorSearchProfile = "config2",
    },
    new SearchField("vector4", new SearchFieldDataType("Collection(Edm.Single)"))
    {
        Retrievable = true,
        Searchable = true,
        Dimensions = 32,
        VectorSearchProfile = "config4",
    },
    new SearchField("name", SearchFieldDataType.String)
    {
        Retrievable = true,
        Searchable = true,
        Filterable = true,
        Sortable = true,
        Facetable = true,
        Analyzer = LexicalAnalyzerName.EnLucene,
    },
    new SearchField("description", SearchFieldDataType.String)
    {
        Retrievable = true,
        Searchable = true,
        Filterable = true,
        Sortable = true,
        Facetable = true,
        Analyzer = LexicalAnalyzerName.StandardLucene,
    },
    new SearchField("category", SearchFieldDataType.String)
    {
        Retrievable = true,
        Searchable = true,
        Filterable = true,
        Sortable = true,
        Facetable = true,
        Analyzer = LexicalAnalyzerName.EnLucene,
    },
    new SearchField("ownerId", SearchFieldDataType.String)
    {
        Retrievable = true,
        Searchable = true,
        Filterable = true,
        Sortable = true,
        Facetable = true,
        Analyzer = LexicalAnalyzerName.EnLucene,
    },
    new SearchField("price", SearchFieldDataType.Double)
    {
        Retrievable = true,
        Filterable = true,
        Sortable = true,
        Facetable = true,
    }
})
{
    ScoringProfiles = {new ScoringProfile("stringFieldBoost")
    {
        Text = new TextWeights(new Dictionary<string, double>
        {
            ["name"] = 3,
            ["description"] = 1,
            ["category"] = 2,
            ["ownerId"] = 1
        }),
        Functions = {new TagScoringFunction("category", 2, new TagScoringParameters("categoryTag"))},
    }},
    DefaultScoringProfile = "stringFieldBoost",
    CorsOptions = new CorsOptions(new string[] { "https://www.example.com/foo" })
    {
        MaxAgeInSeconds = 10L,
    },
    Suggesters = { new SearchSuggester("sg", new string[] { "category", "ownerId" }) },
    Analyzers = {new CustomAnalyzer("tagsAnalyzer", LexicalTokenizerName.Standard)
    {
        TokenFilters = {TokenFilterName.CommonGram},
        CharFilters = {CharFilterName.HtmlStrip},
    }},
    Tokenizers = {new LuceneStandardTokenizerV2("my_tokenizer")
    {
        MaxTokenLength = 100,
    }},
    TokenFilters = {new AsciiFoldingTokenFilter("my_tokenFilter")
    {
        PreserveOriginal = false,
    }},
    CharFilters = { new MappingCharFilter("my_mapping", new string[] { ".=>,", "_=>-" }) },
    Normalizers = {new CustomNormalizer("my_normalizer")
    {
        TokenFilters = {new TokenFilterName("my_tokenFilter")},
        CharFilters = {new CharFilterName("my_mapping")},
    }},
    Similarity = new BM25SimilarityAlgorithm
    {
        K1 = 10,
        B = 0.1,
    },
    Semantic = new SemanticSearch
    {
        DefaultConfiguration = "testconfig",
        Configurations = {new SemanticConfiguration("testconfig", new SemanticPrioritizedFields
        {
            TitleField = new SemanticField("category"),
            PrioritizedContentFields = {new SemanticField("description")},
            PrioritizedKeywordsFields = {new SemanticField("ownerId")},
        })
        {
            FlightingOptIn = true,
        }},
    },
    VectorSearch = new VectorSearch
    {
        Profiles = {new VectorSearchProfile("config1", "cosine")
        {
            Vectorizer = "openai",
            Compression = "mySQ8",
        }, new VectorSearchProfile("config2", "euclidean")
        {
            Vectorizer = "custom-web-api",
            Compression = "mySQ8",
        }, new VectorSearchProfile("config3", "dotProduct")
        {
            Vectorizer = "custom-web-api",
            Compression = "myBQC",
        }, new VectorSearchProfile("config4", "dotProduct")
        {
            Vectorizer = "custom-web-api",
            Compression = "myBQWithoutOriginals",
        }},
        Algorithms = {new HnswAlgorithmConfiguration("cosine")
        {
            HnswParameters = new HnswParameters
            {
                Metric = VectorSearchAlgorithmMetric.Cosine,
            },
        }, new HnswAlgorithmConfiguration("euclidean")
        {
            HnswParameters = new HnswParameters
            {
                Metric = VectorSearchAlgorithmMetric.Euclidean,
            },
        }, new HnswAlgorithmConfiguration("dotProduct")
        {
            HnswParameters = new HnswParameters
            {
                Metric = VectorSearchAlgorithmMetric.DotProduct,
            },
        }},
        Vectorizers = {new AzureOpenAIVectorizer("openai")
        {
            AzureOpenAIParameters = new AzureOpenAIVectorizerParameters
            {
                ResourceUri = new Uri("https://test-sample.openai.azure.com/"),
                DeploymentId = "model",
                ApiKey = "api-key",
                ModelName = AzureOpenAIModelName.TextEmbedding3Large,
            },
        }, new WebApiVectorizer("custom-web-api")
        {
            CustomWebApiParameters = new WebApiVectorizerParameters
            {
                Uri = new Uri("https://my-custom-endpoint.org/"),
                HttpHeaders =
                {
                    ["header1"] = "value1",
                    ["header2"] = "value2"
                },
                HttpMethod = "POST",
                Timeout = XmlConvert.ToTimeSpan("PT1M"),
                AuthResourceId = "api://f89d1c93-58a7-4b07-9a5b-5f89048b927b",
                AuthIdentity = new SearchIndexerDataNoneIdentity(),
            },
        }, new AMLVectorizer("aml")
        {
            AmlParameters = new AMLParameters(new Uri("https://my-custom-endpoint.org/"))
            {
                ResourceId = "aml resource id",
                Timeout = XmlConvert.ToTimeSpan("PT1M"),
                Region = "aml region",
                ModelName = AIFoundryModelCatalogName.OpenAICLIPImageTextEmbeddingsVitBasePatch32,
            },
        }},
        Compressions = {new ScalarQuantizationCompression("mySQ8")
        {
            ScalarQuantizationParameters = new ScalarQuantizationParameters
            {
                QuantizedDataType = VectorSearchCompressionTarget.Int8,
            },
            RescoringOptions = new RescoringOptions
            {
                EnableRescoring = true,
                DefaultOversampling = 10,
                RescoreStorageMethod = VectorSearchCompressionRescoreStorageMethod.PreserveOriginals,
            },
            TruncationDimension = 2,
        }, new BinaryQuantizationCompression("myBQC")
        {
            RescoringOptions = new RescoringOptions
            {
                EnableRescoring = true,
                DefaultOversampling = 10,
                RescoreStorageMethod = VectorSearchCompressionRescoreStorageMethod.PreserveOriginals,
            },
            TruncationDimension = 2,
        }, new BinaryQuantizationCompression("myBQWithoutOriginals")
        {
            RescoringOptions = new RescoringOptions
            {
                EnableRescoring = true,
                DefaultOversampling = 10,
                RescoreStorageMethod = VectorSearchCompressionRescoreStorageMethod.DiscardOriginals,
            },
            TruncationDimension = 2,
        }},
    },
    ETag = "0x1234568AE7E58A1",
};
Response<SearchIndex> response = await client.CreateOrUpdateAsync("temp-preview-test", index);

